function Skill() {

  let points = [];
let position = {x: 0, y: 0};
let prevPosition = {x: 0, y: 0};

function setup(){
  createCanvas(windowWidth, windowHeight)
}

function draw(){
  background(0,104,71);
  
  noStroke();
  fill(237,188,215);
  
  let dx = mouseX - pmouseX;
  let dy = mouseY - pmouseY;
  
