//   points.push(position);  
  
  if(dx || dy){
    position = {x: mouseX, y: mouseY};
    
    if(points.length >= 50){
      points.splice(0, 1);
    }
    
    points.push(position);
  }
  
  points.forEach(point => {
    ellipse(point.x, point.y, 250,250)
  });
}
  return (
    <div>Skill</div>
  )
}

export default Skill
