import React from 'react'
import '../style/skill.css'
import { gsap } from "gsap";

const { useLayoutEffect, useRef } = React;

function Skill() {


for (let i = 1; i<=100; i++){
let box =document.createElement('div');
box
}

  let FollowBox = "#Wrap .FollowBox";
  gsap.set(FollowBox, {
      xPercent: -50, yPercent: -50, scale: 0,
  });
  
  window.addEventListener('mousemove', e => {
      gsap.to(FollowBox, 0.5, {
          x: e.clientX, y: e.clientY, stagger: -0.1, ease: "none", rotate:(i,target)=>{return(i+1) *0.2}
      });
  
      let TL = gsap.timeline({
          defaults: { duration: 3, ease: "none", },
      })
      TL.to(FollowBox, {
          scale: 1,
          stagger: { amount: 0.15, from: "start", ease: "none", }
      })
      TL.to(FollowBox, {
          scale: 0,
          stagger: { amount: 0.15, from: "end", ease: "none", }
      }, "<+=2.5")
  });

  return (
    <div classNameName='skill section'>
 
 <div id="Wrap">
<div className="FollowBox">Just <span> 4</span> fun</div>
  </div>


	</div>

 
  )
}

export default Skill
