import React from 'react'
import '../style/home.css'
import heroImg from '../images/home-img.png'
function Home() {

  const wrapper = document.getElementById("wrapper");

  for (let i = 1; i < 51; i++) {
    let div = document.createElement("div");
    animeCircle = wrapper.appendChild(div);
    animeCircle.classList.add("anime-circle");
  }
  
  let circles = document.querySelectorAll(".anime-circle");
  
  let randomAnimation = anime({
    targets: circles,
    background: () => {
      let hue = anime.random(0, 360);
      let saturation = 60;
      let lumonisity = 70;
      let hslValue = "hsl(" + hue + "," + saturation + "%," + lumonisity + "%)";
      return hslValue;
    },
    borderRadius: ()=> {
      return anime.random(25,50);
    },
    translateX: ()=> {
      return anime.random(-80,80) + "vh";
    },
    translateY: ()=> {
      return anime.random(-80,80) + "vh" 
    },
    scale: ()=> {
      return anime.random(0.45,1.75);
    },
    duration: ()=> {
      return anime.random(250,1500);
    },
    delay: ()=> {
      return anime.random(500,1000);
    },
    loop: true,
    direction: "alternate",
    easing: "easeOutExpo"
  });
  
  
  return (
    <div id='Home' className='home section'>
      <div className="content wrapper" id="wrapper" >


 
                <img src={heroImg} alt="" className='hero-img' />

       <p >Hi I'm Kevin <br /> A Computer Technician and Aspiring Front-End Web Developer <br />Web Design <br />Ui/ux Developer</p>
     
        
   
    
       <div class="buttons">
         <button class="btn resume">Resume</button><br />
         <button class="btn contact">Contact me</button>
       </div>
       <div class="social-icon">
        <a href="#Home"><i class="icon-hov fab fa-viber"></i></a> 
        <a href="#Home">  <i class="icon-hov fab fa-facebook-f"></i></a> 
        <a href="#Home">    <i class="icon-hov fab fa-linkedin-in"></i></a> 
        <a href="#Home">   <i class="icon-hov fas fa-at"></i></a> 

         </div>
     </div>
            
         
    </div>
  )
}

export default Home
