import React from 'react'
import '../style/skill.css'
import { gsap } from "gsap";

const { useLayoutEffect, useRef } = React;

function Skill() {


  const app = useRef();
  
  useLayoutEffect(() => {
    let ctx = gsap.context(() => {
      gsap.to(".box", { rotation: "+=360" });
    }, app);
    
    return () => ctx.revert();
  });



  return (
    <div ref={app} className='section'>
 

  <p></p>

	</div>

 
  )
}

export default Skill
